targets.expr:
sum(
  rate(
    nginx_ingress_controller_requests{ingress=~"$ingress", controller_pod=~"$pod", path=~"$path", status!~"5.*"}[${__range_s}s]
      )
   ) by (ingress)
/ 

sum(
  rate(
    nginx_ingress_controller_requests{ingress=~"$ingress", controller_pod=~"$pod", path=~"$path"}[${__range_s}s]
      )
    ) by (ingress)

histogram_quantile(
  0.99,
  sum by (le)(
    rate(
      nginx_ingress_controller_request_duration_seconds_bucket{
        status!="404|500|304|499",
        ingress=~"$ingress", 
        controller_pod=~"$pod", 
        path=~"$path"}[$__interval]
    )
  )
)

